-- Records of admin_pages
-- ----------------------------
INSERT INTO `admin_pages` VALUES ('1', '1', '2', '微信公众号', 'wei-xin-gon', '', '公众号', '微信公众号是开发者或商家在微信公众平台上申请的应用账号，该帐号与QQ账号互通，通过公众号，商家可在微信平台上实现和特定群体的文字、图片、语音、视频的全方位沟通、互动 。形成了一种主流的线上线下微信互动营销方式。', '', '<p><br>\r\n微信公众号是开发者或商家在微信公众平台上申请的应用账号，该帐号与QQ账号互通，通过公众号，商家可在微信平台上实现和特定群体的文字、图片、语音、视频的全方位沟通、互动 。形成了一种主流的线上线下微信互动营销方式。<br>\r\n正如线上线下微信互动营销的代表微部落，率先提出标准的行业通用模板和深定制的微信平台开发理念相结合。形成了线上线下微信互动营销的开放应用平台。 （这是详细页面的内容）</p>\r\n', '2016-03-22 06:51:48', '2016-03-31 07:43:55', null, null, '个', null, '');
INSERT INTO `admin_pages` VALUES ('2', '1', '2', '网站优化', 'wang-zhan-y', '', '建设、网站、', ' 狭义的网站优化，即搜索引擎优化（SEO），是一种利用搜索引擎的搜索规则来提高目的网站在有关搜索引擎内的排名的方式。', '', '<p> 狭义的网站优化，即搜索引擎优化（SEO），是一种利用搜索引擎的搜索规则来提高目的网站在有关搜索引擎内的排名的方式。<br>\r\nseo网站优化可以提高网站在搜索引擎的排名以及影响品牌知名度、流量水平和投资回报率。然而，网站完全兼容于搜索引擎是一个复杂的过程，涉及到许多不同的seo网站优化因素和组件，以确保每一个环节是以最好的方式处理。</p>\r\n\r\n<p>广义的网站优化所考虑的因素不仅仅是搜索引擎，也包括充分满足用户的需求特征、清晰的网站导航、完善的在线帮助等，在此基础上使得网站的功能和信息发挥出最好的效果。同时也要以企业网站为基础，与网络服务商（如搜索引擎等）、合作伙伴、顾客、供应商、销售商等网络营销环境中各方面因素建立良好的关系。搜索引擎会将站点彼此间的内容做一些相关性的数据对比，然后再由浏览器将这些内容以最快速且接近完整的方式，呈现给搜索者。</p>\r\n', '2016-03-22 05:17:39', '2016-03-31 07:33:36', null, null, '个', null, '');
INSERT INTO `admin_pages` VALUES ('3', '1', '2', '行业平台开发', 'ping-tai-ka', '', '开发,平台', '平台开发、搭建', '', '<p>为行业提供专业的oto网络营销解决方案。</p>\r\n\r\n<p>为企业提供专业的oto解决方案，解决企业烦恼。</p>\r\n\r\n<p><a href=\"http://baike.baidu.com/view/71517.htm\" style=\"color: rgb(19, 110, 194); text-decoration: none; font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px;\" target=\"_blank\">网站开发</a><span style=\"font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px;\">是制作一些专业性强的网站，比如说动态</span><a href=\"http://baike.baidu.com/view/828.htm\" style=\"color: rgb(19, 110, 194); text-decoration: none; font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px;\" target=\"_blank\">网页</a><span style=\"font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px;\">。ASP、PHP、JSP网页。而且网站开发一般是原创，</span><a href=\"http://baike.baidu.com/view/10410.htm\" style=\"color: rgb(19, 110, 194); text-decoration: none; font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px;\" target=\"_blank\">网站制作</a><span style=\"font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px;\">可以用别人的模板。网站开发字面意思比制作有更深层次的</span><a href=\"http://baike.baidu.com/subview/716211/8059316.htm\" style=\"color: rgb(19, 110, 194); text-decoration: none; font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px;\" target=\"_blank\">进步</a><span style=\"font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px;\">，它不仅仅是网站美工和内容，它可能涉及到域名注册查询、网站的一些功能的开发。对于较大的组织和企业，网站开发团队可以由数以百计的人（web开发者）组成。规模较小的企业可能只需要一个永久的或收缩的网站管理员，或相关的工作职位，如一个平面设计师和/或信息系统技术人员的二次分配。Web开发可能是一个部门，而不是域</span><a href=\"http://baike.baidu.com/view/557606.htm\" style=\"color: rgb(19, 110, 194); text-decoration: none; font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px;\" target=\"_blank\">指定</a><span style=\"font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px;\">的部门之间的协作努力。</span></p>\r\n', '2016-03-23 02:30:41', '2016-04-05 03:36:25', null, null, '个', null, '');
INSERT INTO `admin_pages` VALUES ('4', '1', '2', '网络营销推广', 'wang-luo-yi', '', '推广，网络，营销', '在营销活动中通过新闻的形式和手法，多角度、多层面地诠释企业文化、品牌内涵、产品机理、利益承诺，传播行业资讯，引领消费时尚，指导购买决策。', '', '<p>营性的专业部门负责，比如网站建设、平台优化、关键字点击推广、付费推广、平面推广、隐性推广、人脉推广、品牌推广等等。<br>\r\n在营销活动中通过新闻的形式和手法，多角度、多层面地诠释企业文化、品牌内涵、产品机理、利益承诺，传播行业资讯，引领消费时尚，指导购买决策。这种模式非常有利于引导市场消费，此外，新闻联播还有效综合运用新闻报道的传播手段，创造最佳的传播效果，能够在短期内提升企业品牌的曝光率，提升产品的知名度，塑造品牌的美誉度和公信力。<br>\r\n营销与推广也是关连的，像不少中小企业或个体卖家，以营销带动推广，即是说他们在不懂得怎样去推广前，先按线下惯例踏实地做，先赢人脉、赢人气，用口碑来推广，这样他们在不知不觉间就为自己的营销做了推广。<br>\r\n网络营销推广还分，区域推广、时效性推广、持久推广及综合推广等。对网络营销推广来说，以综合营销为胜。</p>\r\n\r\n<p> </p>\r\n', '2016-03-23 02:41:14', '2016-03-31 07:29:31', null, null, '个', null, '');
INSERT INTO `admin_pages` VALUES ('5', '1', '2', '网站管理', 'wang-zhan-g', '', '优化，网站', '网站管理是对网络营销既基础又重要的一个环节。', '', '<p>网站管理是对网络营销既基础又重要的一个环节，现时大多数中小企业开始重视网络营销，慢慢参与一些培训课程；改建企业网站；实施SEO；邮件营销；病毒营销；软文营销；博客营销；微博营销；论坛营销等等网络推广，甚少注意自身的网站管理问题；人员的配备；要求；制度；实施；监管；反馈。要做好网络营销就必须解决网络营销的基础问题。基础没处理好后面的一切都是空话更不用说能实现一个怎么样的效果，正因如此网站管理也成为企业在网络营销进程中的一个重要课题。</p>\r\n', '2016-03-23 02:42:59', '2016-03-31 07:32:08', null, null, '个', null, '');
INSERT INTO `admin_pages` VALUES ('8', '1', '4', '英国独立报停刊：那些值得记住的经典头版', 'zi-ye-mian', '', 'new——list——first', '  反伊战、支持公益、同情难民的英国独立报，昨天出版最后一期报纸，30年可圈可点的历程画上阶段性句号，之后将专注于在线新闻。让我们一起回顾一下它做过的精彩头版', '/templetes/meetiao/assets/blog/on_the_mountains.jpg', '<p>我是一个列表页面，哈哈哈哈哈哈哈</p>\r\n', '2016-03-25 02:39:23', '2016-03-31 04:32:39', null, null, '个', null, '');
INSERT INTO `admin_pages` VALUES ('9', '1', '4', '西方国家频发恐袭 该不该和宗教扯上关系 ', 'this2', '', 'this2', '  比利时首都布鲁塞尔发生了连环爆炸事件。现已确定该事件是由IS所策划实施。但是当人们在谴责凶手的同时，容易将事件扩大化以及关联化。于是，宗教渐渐成为众矢之的。', '/templetes/meetiao/assets/blog/on_the_mountains.jpg', '<div class=\"img_wrapper\"><img alt=\"\" data-link=\"\" src=\"http://n.sinaimg.cn/news/transform/20160324/d2sU-fxqtiwa5031108.jpg\" class=\"img-responsive\"></div>\r\n\r\n<p>　　近年来，西方世界遇到了许多前所未见的问题：移民与治安、宗教极端主义与恐怖袭击、满眼的清真寺与身边“格格不入”的陌生人……苦恼与不解中，人们亟需一个高度概括且无需思考的答案聊以自慰。</p>\r\n\r\n<div class=\"img_wrapper\">\n<img alt=\"比利时首都布鲁塞尔发生连环爆炸\" data-link=\"\" src=\"http://n.sinaimg.cn/news/transform/20160324/9jdG-fxqtiwa5031111.jpg\" class=\"img-responsive\"><span class=\"img_descr\">比利时首都布鲁塞尔发生连环爆炸</span>\n</div>\r\n\r\n<p>　　就在笔者发稿的前一天，欧洲小国比利时首都布鲁塞尔发生了连环爆炸事件。现已确定该事件是由极端组织“伊斯兰国”所策划实施。但是当人们在谴责凶手的同时，容易将事件扩大化以及关联化。</p>\r\n\r\n<p>　　于是，宗教渐渐成为众矢之的，阿拉伯头巾变成了穆斯林符号，土耳其裔德国人变成了德国穆斯林……对伊斯兰教的成见、恐惧与仇视在西方蔓延开来。西方是民主的，民众的呼声会在冲突中上升为政党意志甚至国家行为。此情此景，西方政治真可谓滑稽可笑，荒诞不经。</p>\r\n\r\n<div class=\"img_wrapper\">\n<img alt=\"美国穆斯林\" data-link=\"\" src=\"http://n.sinaimg.cn/news/transform/20160324/f5p9-fxqswxn6366041.jpg\" class=\"img-responsive\"><span class=\"img_descr\">美国穆斯林</span>\n</div>\r\n\r\n<p>　　美国大选中共和党初选候选人唐纳德-特朗普一路过关斩将。去年年底，当他表示“美国应阻止所有穆斯林入境”后，其支持率不降反升。作为一个多元化的移民国家，这样的想法与美国的根基相违背。</p>\r\n\r\n<p>　　欧洲的问题注定更为严重。2015年英国大选，由于选举制度问题，极右翼、反移民的英国独立党（UKIP）虽仅获得一个议席却得到了12.7% 的选票，得票数仅次于保守党与工党。这意味着英国社会中的疑欧主义和反对移民政策的民意力量在增加。匈牙利总理维克多-奥尔班甚至也公开表示，“穆斯林难 民涌入会威胁欧洲的基督教属性”。而在法德等其他欧洲国家，民粹主义与极右翼思想相结合并转化为政治力量的事例更是层出不穷。</p>\r\n\r\n<p>　　与美国不同，欧洲诸国从来就不是传统意义上的移民国家，多元文化的理念多半只是一个光鲜的幌子。英国的女王，法国的“世俗化”，无论保守还是开放，他们总有一条不可由民众僭越或塑造的“权威”。</p>\r\n\r\n<p>　　欧洲欢迎移民带来他们所或缺的东西，无论是美食，技术，还是劳力。而对于固有文化结构的松动，欧洲嗅觉异常灵敏。他们一直以来的苦恼可以概括为 “我引入的是‘工人’，得到的却是‘人’。”人便不只有吃饭和工作，人便会有自己的群体，自己的文化。这各中矛盾之于西方世界是由来已久的。移民问题或者 所谓的“移民融入主流社会”的尝试是西方的固有问题。</p>\r\n\r\n<p>　　但如今，面对突如其来的难民潮与更加复杂的情势，人们慌了手脚，更愿意将一切武断地归纳为“西方世界与穆斯林的冲突”。这样一个简单又看似合理 的解释像一针吗啡，缓解了西方痛苦的思考。但后果是：原本复杂却可解的社会问题变成了简单却无解的宗教仇视。这不仅对于移民是不公平的，对于西方本身更是 无比危险的。</p>\r\n\r\n<p>　　<strong>在讨论其危险性之前，我们不妨先梳理一下移民问题的实质。西方流行着这样一个笑话：</strong></p>\r\n\r\n<p>　　一位德国医生与一位富足而体面的土耳其移民比邻而居，生活美好，相处融洽。一天，德国医生遇到了这位土耳其邻居，便攀谈道：“我的朋友，看，我 们的生活是多么美好而融洽！我有洋房你也有洋房，我有跑车你也有跑车，我的院子里修了游泳池，你的院子里也有。”土耳其移民笑了笑说：“不，先生，我比你 体面得多！”“为什么？”德国医生不解地问道。土耳其移民回答说：“因为我的邻居是一位德国医生，而你的邻居是一个土耳其移民。”</p>\r\n\r\n<p>　　事实上，在土裔德国人成为“德国穆斯林”之前，这则故事就已经流传甚广。移民本身会带来社会问题，并与主流社会形成矛盾，这一切并不是因为宗教而起。抛开宗教因素，移民本身就是很难为西方主流社会所彻底接纳的。</p>\r\n\r\n<p>　　近来热议的美国唐人街消逝与其争议问题就说明，即便是在最“包容”的美国社会与最“温和”的华人移民之间，问题也是存在的。放眼世界，几乎每一 座国际化大都市都伴随着移民带来的社会治安等一系列问题。诸如此类问题自然难解但并非不可解。但如今的西方世界，在难民潮的冲击下，惶恐与无助之中，不再 将自己的移民难民问题视为传统社会问题的一部分，放弃对症下药，转而述诸宗教威胁与文明冲突论，并荒谬地试图予以解决，这便促成了上文谈到的西方民粹极右 翼势力的歇斯底里。</p>\r\n\r\n<p>　　身处西方社会的移民或难民，绝大多数来自于经济欠发达国家的落后地区，他们的流向又往往是最发达国家的现代化大都市，这层矛盾本身就足以解释很 多问题。例如来自经济欠发达、教育水平低下地区的移民难民往往重男轻女现象比较严重，思想相对保守，当他们突然置身于现代化大都市中时，接触到诸如女权问 题、LGBT群体认识问题时自然会产生抵触，正如人类的认识也是从落后到先进逐步发展到今天一样。</p>\r\n\r\n<p>　　但是，当人们认为他们的保守观点是出于宗教意识而忽略物质基础时，这种落后思想反而会借由宗教工具变本加厉，哪怕他们中很多人只是出身而为穆斯林并一直过着世俗化的生活。</p>\r\n\r\n<p>　　于是，原本可通过教育或环境影响来慢慢解决的落后思想问题，变成了无法解决的宗教仇视问题，并进一步将移民难民推向孤立面与对立面，为宗教极端化推波助澜。</p>\r\n\r\n<p>　　有人说，“移民”是除去“死亡”外人类最难面对的事情。人们为了更好的生活或甚至是迫于生命威胁而背井离乡来到举目无亲的地方，即便最终能够过上富足的生活，其精神上的流放所带来的痛苦亦是常人无法想象的。</p>\r\n\r\n<p>　　上个世纪，很多身在德国的土耳其老人会用一周时间守在收音机旁等候只一个小时的土耳其语节目，并将其录下来反复收听那久违的乡音。在欧洲，背井 离乡的人们在最熟悉的地方用最熟悉的方式与老友相聚攀谈，但只因为这一切发生在与周遭“格格不入”的建筑里并伴随着陌生而“刺耳”的宣礼，周五清真寺中的 感人的场景便不再被祝福了。很多时候，人一出来便回不去了。而对于很多身在欧洲的北非移民，在法国，他们是摩洛哥人；在摩洛哥，他们是法国人。总之，他们 永远处于一种身份的流放之中无法逃遁 。</p>\r\n\r\n<div class=\"img_wrapper\">\n<img alt=\"法国南特一清真寺中礼拜的人们\" data-link=\"\" src=\"http://n.sinaimg.cn/news/transform/20160324/ykyt-fxqtiwa5031166.jpg\" class=\"img-responsive\"><span class=\"img_descr\">法国南特一清真寺中礼拜的人们</span>\n</div>\r\n\r\n<p>　　终于有一天，文章起笔提到的情境发生了。于是，“你们说我是穆斯林，那我就是穆斯林吧！”毕竟，穆斯林算是一个放之四海都不会丢失的，移民们那久违的“身份认同”。</p>\r\n\r\n<p>　　上文说到，西方世界的移民难民由于种种原因渐渐被贴上了“西方穆斯林”的标签并被置于主流社会的对立面。这给予了宗教极端主义极佳的可乘之机。 西方社会自然不是问题产生的全部原因，恶性循环中的另一半便要归咎于宗教本身。西方世界有太多的“穆斯林”这并不可怕，可怕的是有太多的，多到不成比例的 “伊斯兰”。</p>\r\n\r\n<p>　　在欧洲的尽头冰岛，上世纪七十年代，这里还只有七位穆斯林。但在不到半个世纪的时间里，这一数字上涨为1500位。</p>\r\n\r\n<p>　　为数不多的清真寺分为两派，一派由埃及爱资哈尔学者主持，持Wasati即温和派观点。这种观点强调伊斯兰应顺应社会发展，在教法上强调对《古 兰经》及《圣训》等宗教经典进行Ijtihad 即教法解释，使伊斯兰教成为一种“简单易行”的宗教以吸引受众。与其相对的是由沙特阿拉伯及海湾国家出资建设的萨拉菲派（Salafi）清真寺，他们所强 调的是萨拉菲思想，一切严格参照伊斯兰经典。</p>\r\n\r\n<p>　　反映在现实当中，两派对诸多发生在西方的宗教问题进行着完全不同的教法诠释。比如，冰岛由于地处高纬度地区，夏季日照时间长似极昼，那么在斋月 Ramadan时穆斯林该如何禁食。Salafi认为应严格按照当地日出日落时间禁食，哪怕以健康为代价。Wasati则认为由于地理条件不同，当地穆斯 林按照麦加时间禁食即可。这种教法上的分歧存在于整个西方世界，涉及穆斯林生活的方方面面，比如在西方俱乐部效力的穆斯林球员是否可以身着印有啤酒或赌博 广告的队服等等。</p>\r\n\r\n<p>　　Wasati派学者认为西方穆斯林应当摒除全盘西化和拒绝融入西方社会的两种极端，在恪守信仰的同时积极融入西方社会，通过成为社会的表率，做 伊斯兰在西方的“大使”，增进西方对伊斯兰教的理解，积极消除仇视与隔阂。而Salafi派则秉持相反的观点，坚持原教旨主义与极端宗教思想，不与西方社 会妥协，在西方制造穆斯林与“堕落的物质文明”间的壁垒。对此，沙特阿拉伯与卡塔尔等海湾国家难辞其咎。在这些国家的资助下，西方兴建其一座又一座萨拉菲 清真寺，对欧洲社会安全造成严重威胁，使“伊斯兰”充溢欧洲，对穆斯林背景的移民难民融入社会构成阻碍。</p>\r\n\r\n<p>　　2015年法国警方的一份调查显示，在2500个被调查宗教群体中，萨拉菲性质的伊斯兰群体达到90个，数字比五年前整整翻了一倍。巴黎恐怖袭击后，德国副总理加布里尔公开批评道：危险的极端宗教主义者都来自沙特资助的萨拉菲清真寺，沙特政府应该停止此类行为。</p>\r\n\r\n<p>　　德国副总理点出了西方社会亟需注意的问题。沙特王国，以萨拉菲主义与瓦哈比主义为立国之本，向西方世界输出极端宗教思想的行为是西方伊斯兰问题 的关键。在西方社会对穆斯林群体施以斥力的同时，欧洲随处可见的萨拉菲清真寺、萨拉菲书店及伊斯兰学校又对该群体产生了拉力，在斥力与拉力的共同作用下， 被脸谱化西方穆斯林群体很难真正成为西方社会的一员，且在极端思想作用下对西方国家安全构成严重威胁。</p>\r\n', '2016-03-25 05:42:13', '2016-03-31 08:15:02', null, null, '个', null, '');
INSERT INTO `admin_pages` VALUES ('10', '1', '3', '圣仙果品', 'xiang-mu-yi', '', '圣仙果营销', '圣仙果营销', '/templetes/meetiao/img/kao.jpg', '<h1 style=\"margin: 0px; padding: 0px; font-size: 20px; text-align: center; line-height: 35px; color: rgb(102, 102, 102); font-family: \'Microsoft YaHei\';\">山东曲阜市圣仙果品营销型网站案例展示</h1>\r\n\r\n<div class=\"info_con_tit\" style=\"margin: 0px 0px 15px; padding: 0px; font-family: 宋体; line-height: 24px; color: rgb(102, 102, 102); font-size: 12px;\">\r\n<div class=\"info_con_tit\" style=\"margin: 0px 0px 15px; padding: 0px; line-height: 24px;\">\r\n<div class=\"n_tit\" style=\"margin: 0px; padding: 0px; text-align: center; color: rgb(188, 187, 187);\">\n<span style=\"margin-right: 20px;\">文章出处：</span><span style=\"margin-right: 20px;\">责任编辑：</span><span style=\"margin-right: 20px;\">作者：</span><span class=\"m_qrinfo\" style=\"display: inline-block; position: relative; z-index: 100; margin-right: 20px;\"><a href=\"http://www.nsw88.com/Mobile/MArticles/shengxianguopinyingx_page1.html\" looyu_bound=\"1\" style=\"color: rgb(188, 187, 187); text-decoration: none;\" title=\"查看手机网址\">查看手机网址</a></span><span style=\"margin-right: 20px;\">发表时间：2015-02-06 10:14:00</span>\n</div>\r\n</div>\r\n</div>\r\n\r\n<div id=\"cntrBody\" style=\"margin: 0px; padding: 0px; font-family: \'Microsoft YaHei\'; font-size: 12px; line-height: 14.772px;\">\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\"> </p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\"><span style=\"color: rgb(255, 102, 0);\"><span style=\"font-size: 18px;\"><strong>圣仙果项目情况</strong></span></span></p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\">   <strong>上线时间：</strong>2014年3月</p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\"><strong>   所属行业</strong>：水果批发</p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\"><strong>   盈利模式</strong>：产品展示 </p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\"><strong>   公司介绍</strong>：</p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\">  曲阜市圣仙果品有限公司总部设在素有“文化圣地，度假天堂”之美誉的孔子故里山东省曲阜市，南北接壤104国道与京台高速，地理位置优越，交通环境便利，是鲁西南重要的中心城市之一。</p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\"> </p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\"><span style=\"color: rgb(255, 102, 0);\"><span style=\"font-size: 18px;\"><strong>圣仙果王总分享</strong></span></span></p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\"><img alt=\"/templetes/meetiao/img/ren3.jpg\" class=\"img-responsive\" src=\"http://img.nsw88.com/FCK/2015-02/2015020602D62VDRD6.jpg\">  <font face=\"Arial, Verdana, sans-serif\">之前我做网络营销做的很累，花钱也多，但就是没有效果（包括：建网站、做推广、开诚信通的种种问题）我现在一清二楚，学习之后再做这些我们做到了让每一分钱产生效益。</font></p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\"><font face=\"Arial, Verdana, sans-serif\">  通过做网络营销我们取得很好的成绩，因为我们的产品很有季节性，所以我有充足的时间在产品销售之前做好网络布局，再加上再牛商网建的营销型网站，转化量也提高了，现在我们的产品确实不够卖，这一点对我做电子商务的信心大大提高，相信收益会越来越高</font><font face=\"Arial, Verdana, sans-serif\">。</font></p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\"> </p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\"><span style=\"color: rgb(255, 102, 0);\"><span style=\"font-size: 18px;\"><strong>圣仙果品网络营销预览</strong></span></span></p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px; text-align: center;\">   <img alt=\"/templetes/meetiao/img/list.jpg\" class=\"img-responsive\" src=\"http://img.nsw88.com/FCK/2015-02/20150206F6HZJ8X24R.jpg\"></p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\">  首屏告诉客户我们是中国第一家有机大樱桃种植企业，并且济宁市果树标准化范园，产品无农药，无残留，带给您绿色健康品质，打消客户疑虑，购买的放心。</p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\">  产品分类处按照产品的特性分类，方便客户查找对号入座，也利于百度搜索引擎。</p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\"><font face=\"Arial, Verdana, sans-serif\">  网站以绿色为底蕴，充分展示产品无公害，有机食品，奏响绿色主旋律，突出主题，再加上资质认证，客户放心购买。</font></p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\"><font face=\"Arial, Verdana, sans-serif\">  </font><span style=\"font-family: Arial, Verdana, sans-serif;\">客户见证，从经销商的角度出发，讲解参与的整个过程，增加客户的信赖度。</span></p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\">圣仙果品公司特点</p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\">  公司是2007年成立的当时王总就认识到了电子商务的重要性，知道后期一定要发展网络营销，所以早早的建了网站，连续做了几年的付费推广，三年的诚信通，认为这就是电子商务，但一直是投入却没产出的结果，让我对电子商务很失望。</p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\">  参加单仁资讯策略班后请牛商网做了营销型网站，现在虽然投付费，但是花的钱少，得到的流量很精准，每天的询盘量也不少，转化量也提高了。现在我们的产品确实不够卖，这一点对我做电子商务的信心大大提高了，后期我们外跑的业务员我会慢慢的减少，增加到电商部门，大家后期业务会越做越轻松，但是收益会越来越高。</p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\"> </p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\"><span style=\"color: rgb(255, 102, 0);\"><span style=\"font-size: 18px;\"><strong>圣仙果品营销网站运营经验</strong></span></span></p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\">  <strong>王昱淇王总说其实经验谈不上只是有些切身的体会：</strong></p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\">  <strong>关于推广</strong>：以前一直认为付费效果好，但是现在我们的推广费用减少了很多，加之做一些适合我们的免费推广，效果确实很明显，学习真的是我们减少摸索的重要途径</p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\">  <strong>关于网站：</strong>以前一直认为一个成熟的公司应该有网站，但是网站有什么用我没有考虑过，更没有想到营销型网站对于成交有那么大的作用。现在我把网站的各项数据指标都当做宝贝。</p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\">  <strong>关于运营</strong>：网络营销不是一个人可以实现的，有了团队分工协作，自上而下会越做越轻松。</p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\">  还有一点就是我的心态我的信心我的希望，这些东西不是用钱可以买来的。我相信我们的成绩会越来越好，期待有一天我可以站在讲台上给大家讲：《曲阜市圣仙果品有限公司》的网络营销之路。</p>\r\n\r\n<div style=\"margin: 0px; padding: 0px;\"> </div>\r\n\r\n<div style=\"margin: 0px; padding: 0px;\"> </div>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\"><span style=\"color: rgb(255, 102, 0);\"><span style=\"font-size: 18px;\"><strong>圣仙果品公司介绍</strong></span></span></p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\">  曲阜市圣仙果品有限公司总部设在素有“文化圣地，度假天堂”之美誉的孔子故里山东省曲阜市，南北接壤104国道与京台高速，地理位置优越，交通环境便利，是鲁西南重要的中心城市之一。</p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\">  曲阜市圣仙果品有限公司成立于2007年3月，注册资金6800万元，公司资产近3.6亿，被评为济宁市农业龙头企业、济宁市林业产业化先进个人、济宁市首批市级果树标准示范园、山东省林业产业化龙头企业、捐资助学先进单位。我公司以种植大樱桃，以有机管理为核心，集种植、优质果品生产、良种苗木繁育、采购、储存、加工、配送、销售于一体的大型果品公司。我公司前身为曲阜九山果品厂，所销售的孔府鲜枣已有30年历史，孔府樱桃、孔府冬枣、孔府鲜枣于1995年已注册商标，授予省著名商标，2009年申请绿色食品，2013年成功申请有机大樱桃。</p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\">  2007年开始我公司投资近亿元，发展樱桃树、银杏树、枣树共3000亩，其中钢架结构冬暖式大棚400亩，集采摘观光旅游于一体的顺祥欢乐农场，有机樱桃栽培等基地，整个园区全部按照有机管理，以通过国家有机认证合格为标准，选用先进的品种，为广大顾客种植真正的国产有机大樱桃。在得天独厚的自然优势下打造曲阜有机大樱桃品牌不断努力，为我国绿色,生态,有机食品事业做出更大的贡献。</p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\"> </p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\"> </p>\r\n</div>\r\n\r\n<div id=\"cntrBody\" style=\"margin: 0px; padding: 0px; font-family: \'Microsoft YaHei\'; font-size: 12px; line-height: 14.772px;\">\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\"> </p>\r\n</div>\r\n', '2016-03-28 03:14:36', '2016-04-13 07:35:35', null, null, '个', null, 'http://www.hoolay.cn/');
INSERT INTO `admin_pages` VALUES ('11', '1', '3', '清谷新禾', 'xiang-mu-er', '', '', '', '/templetes/meetiao/img/qing.jpg', '<p style=\"margin-left:26px;\"> </p>\r\n\r\n<h1 style=\"margin: 0px; padding: 0px; font-size: 20px; text-align: center; line-height: 35px; color: rgb(102, 102, 102); font-family: \'Microsoft YaHei\';\">清谷新禾有机绿色食品营销型网站案例展示</h1>\r\n\r\n<div class=\"info_con_tit\" style=\"margin: 0px 0px 15px; padding: 0px; font-family: 宋体; line-height: 24px; color: rgb(102, 102, 102); font-size: 12px;\">\r\n<div class=\"info_con_tit\" style=\"margin: 0px 0px 15px; padding: 0px; line-height: 24px;\">\r\n<div class=\"n_tit\" style=\"margin: 0px; padding: 0px; text-align: center; color: rgb(188, 187, 187);\"> </div>\r\n</div>\r\n</div>\r\n\r\n<div id=\"cntrBody\" style=\"margin: 0px; padding: 0px; font-family: \'Microsoft YaHei\'; font-size: 12px; line-height: 14.772px;\">\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\"><strong>清谷新禾有机绿色食品项目情况</strong></p>\r\n\r\n<hr>\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\"><strong>  上线时间</strong>：2013年10月</p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\"><strong>  所属行业</strong>：有机食品</p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\"><strong>  盈利模式</strong>：招商加盟、批发代理</p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\"><strong>  主营产品/关键词介绍</strong>：</p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\">  清谷新禾，荞麦产品，有机食品，绿色食品，荞麦挂面。清谷新禾是专心致力有机绿色食品生产及荞麦全产业链建设专业企业。产品涵盖有机绿色食品、荞麦低糖食品、有机大米、荞麦茶、荞麦速食面等。</p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\"> </p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px; font-family: Arial, Verdana, sans-serif;\"><b>清谷新禾有机绿色食品客户特点</b><b>：</b></p>\r\n\r\n<hr style=\"font-family: Arial, Verdana, sans-serif;\">\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\"><font face=\"Arial, Verdana, sans-serif\">  1、2011年之前，集团是以传统营销的商业模式为主体，依靠传统媒体、业务走访等方式寻找客户、客源，以通辽市为中心向外阜辐射开展市场；</font></p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\"><font face=\"Arial, Verdana, sans-serif\">  2、2011年开始开展电子商务，从地理层面上看，近乎全部的市场份额都只是驻步于东北地区和部分华北地区的北方；</font></p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\"><font face=\"Arial, Verdana, sans-serif\">  3、2011年公司销售总额比年初预期增长了37个点。</font></p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\"><font face=\"Arial, Verdana, sans-serif\">  4、2012年销售总额突破了1.6亿。其中电子商务销售占19%，而电子商务平台促使下线销售渠道销售增长达34%，营业额增加了3200多万元。</font></p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\"><font face=\"Arial, Verdana, sans-serif\">  5、2013年清谷新禾与牛商网建立合作关系，在10月中旬门户与营销相结合的清谷新禾新版官网正式上线。</font></p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\"><font face=\"Arial, Verdana, sans-serif\"> </font><strong><font face=\"Arial, Verdana, sans-serif\"> ——营销型网站让传统企业在电子商务领域一展身手！</font></strong></p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\"><strong> </strong></p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\"> </p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\"><b>清谷新禾有机绿色食品之合作前传</b><b style=\"font-family: Arial, Verdana, sans-serif;\"><font face=\"Arial, Verdana, sans-serif\">：</font></b></p>\r\n\r\n<hr>\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\">  1、开展电子商务之前主要在批发市场靠实体店销售，商圈仅限济南和周边县市，批发市场竞争激烈，利润比较低，淡季的时候一天没有一个单子那很正常，房租逐年增长，费用比较高，有个网站，但是特别简陋，几乎没有更新，所以效果不好，业绩一般；</p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\">  2、2012年接触单仁资讯后，试听了蔡勤东老师的课，当场交钱报了策略班，学习回来后立即行动，确定了批发定制盈利模式，规划了网站框架，修改网站，修改了一个多月，感觉不满意；</p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\">  3、最后还是找美调建了新站。</p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\"> </p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\"> </p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\"><font face=\"Arial, Verdana, sans-serif\"><b>清谷新禾有机绿色食品营销型网站展示</b></font></p>\r\n\r\n<hr>\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\">  内蒙古清谷新禾有机食品集团有限责任公司是专心致力有机绿色食品生产及荞麦全产业链建设专业企业。立足于内蒙古大草原，营销覆盖大中华区和全球市场。产品涵盖有机绿色食品、荞麦低糖食品、荞麦家纺产品、植物提取等，是农业产业化国家重点龙头企业、国家级高新技术企业、中国驰名商标获得企业，创建了“中国荞麦交易网”和“荞卖网”2个专业荞麦电子商务平台。</p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px; text-align: center;\"><img alt=\"/templetes/meetiao/img/hean.jpg\" class=\"img-responsive\" src=\"http://img.nsw88.com/FCK/2014-03/2014032468JL22PHD6.jpg\"> <span style=\"font-family: Arial, Verdana, sans-serif;\"> </span></p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\"> </p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\">  网站首页头部以绿色为主色系，展示了清谷新禾核心产品和盈利模式，针对目标客户群展开营销。</p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\">  产品展示部分列出了清谷新禾各个系列的产品和图片，图片简洁清晰，给人淳朴、清洁的感觉。</p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\">  网站中间部分特别展示了清谷新禾的生产基地，给客户信任和安全感。</p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\"> </p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\"><font face=\"Arial, Verdana, sans-serif\"><b>清谷新禾有机绿色食品新老网站PK</b></font></p>\r\n\r\n<hr>\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\"><img alt=\"/templetes/meetiao/img/duibi.png\" class=\"img-responsive\" src=\"http://img.nsw88.com/FCK/2014-03/2014032426FN4PVZ48.jpg\"></p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\">  <strong>老网站网址：</strong></p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\">  搜索引擎优化情况:没有新闻版块，不利于更新，优化非常困难</p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\">  转化率情况:客户很难搜到网站，几乎没有询盘</p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\">  网站整体的运营情况：用户体验感非常弱、客户了解不到产品与公司</p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\"> </p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\">  美调<strong>做的网站：</strong></p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\">  搜索引擎优化情况：重点关键词“荞麦低糖食品招商加盟”和“荞麦速食面招商加盟”均排在百度的首页第1名；“有机绿色食品招商加盟”排在百度首页第2名</p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\">  转化率情况：咨询的基本都是精准客户，询盘转化率大大提升</p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\">  网站整体的运营情况：电子商务平台促使下线销售渠道销售增长达34%，营业额增加了3200多万元</p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px; font-family: \'Microsoft YaHei\';\"><b style=\"font-family: Arial, Verdana, sans-serif;\">清谷新禾有机绿色食品营销型网站推广情况</b></p>\r\n\r\n<hr style=\"font-family: \'Microsoft YaHei\'; font-size: 12px; line-height: 14.772px;\">\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px; font-family: \'Microsoft YaHei\'; text-align: center;\"><img alt=\"/templetes/meetiao/img/tuiguang.jpg\" class=\"img-responsive\" src=\"http://img.nsw88.com/FCK/2014-03/20140324LL2L02FB42.jpg\"></p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px; font-family: \'Microsoft YaHei\';\">  清谷新禾有机绿色食品营销型网站上线之后多个核心关键词排名进入百度首页，流量大幅度提升！  </p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px; font-family: \'Microsoft YaHei\';\"> </p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px; font-family: \'Microsoft YaHei\';\"><b>清谷新禾有机绿色食品营销型网站运营经验分享</b></p>\r\n\r\n<hr style=\"font-family: \'Microsoft YaHei\'; font-size: 12px; line-height: 14.772px;\">\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px; font-family: \'Microsoft YaHei\';\">  <strong>1、网站建设：</strong></p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px; font-family: \'Microsoft YaHei\';\">  公司原先网站是简单模板网站，用户体验感非常弱，客户了解不到产品与公司。通过牛商网的帮助，网站改成营销型网站之后，各方面都非常强了。</p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px; font-family: \'Microsoft YaHei\';\"> </p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px; font-family: \'Microsoft YaHei\';\"> <strong> 2、团队建设：</strong></p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px; font-family: \'Microsoft YaHei\';\">  在每个岗位配备了专业型人才、主要岗位以专人专岗为主。岗位如下：电商经理一名、B2C运营主管一名、B2B运营主管一名、招商专员一名、天猫店铺运营一名、推广专员一名、美工两名、客服数名。</p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px; font-family: \'Microsoft YaHei\';\"> </p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px; font-family: \'Microsoft YaHei\';\"> <strong> 3、推广搭建：</strong></p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px; font-family: \'Microsoft YaHei\';\">  主要技巧有百度免费推广（百度知道、百度百科、百度贴吧等）、百度付费推广、招商加盟型网站广告位推广、邮件推广、微博推广、微信推广等。</p>\r\n\r\n<div style=\"margin: 0px; padding: 0px; font-family: \'Microsoft YaHei\'; font-size: 12px; line-height: 14.772px;\"> </div>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px; font-family: \'Microsoft YaHei\';\"> </p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px; font-family: \'Microsoft YaHei\';\"><font face=\"Arial, Verdana, sans-serif\"><b>清谷新禾有机绿色食品董事长董总分享</b></font></p>\r\n\r\n<hr style=\"font-size: 12px; line-height: 14.772px; font-family: Arial, Verdana, sans-serif;\">\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px; font-family: \'Microsoft YaHei\';\"><img alt=\"/templetes/meetiao/img/ren2.jpg\" class=\"img-responsive\" src=\"http://img.nsw88.com/FCK/2014-03/20140324X2VZF6X2V2.jpg\">  1.做电子商务后，我们的商圈扩大到全国；</p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px; font-family: \'Microsoft YaHei\';\"> </p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px; font-family: \'Microsoft YaHei\';\">  2.我们主要的收获就是网络营销的团队建设起来了，网销团队现已有9个人，状态很好，积极性高，士气高昂；</p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px; font-family: \'Microsoft YaHei\';\"> </p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px; font-family: \'Microsoft YaHei\';\">  3.我们半年时间接单<strong><span style=\"color: rgb(128, 0, 0);\">495单</span></strong>，业绩比去年<span style=\"color: rgb(128, 0, 0);\"><strong>翻倍增长</strong></span>，今年7月份济南某家太阳能热水器厂家与我们<span style=\"color: rgb(128, 0, 0);\"><strong>签约180万元</strong></span>，现在线上业绩已经<span style=\"color: rgb(128, 0, 0);\"><strong>超过线下3倍还多</strong></span>；</p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px; font-family: \'Microsoft YaHei\';\"> </p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\"> </p>\r\n</div>\r\n', '2016-03-28 04:01:37', '2016-04-13 06:19:12', null, null, '个', null, '');
INSERT INTO `admin_pages` VALUES ('13', '1', '3', '益康', 'xiang-mu-si', '', '', '', '/templetes/meetiao/img/yi.jpg', '<p style=\"margin-left:26px;\"> </p>\r\n\r\n<h1 style=\"margin: 0px; padding: 0px; font-size: 20px; text-align: center; line-height: 35px; color: rgb(102, 102, 102); font-family: \'Microsoft YaHei\';\">济南益康食品营销型网站案例</h1>\r\n\r\n<div id=\"cntrBody\" style=\"margin: 0px; padding: 0px; font-family: \'Microsoft YaHei\'; font-size: 12px; line-height: 14.772px;\">\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px; text-align: center;\"><span style=\"font-size: 18px;\"><strong>网络营销缩短与客户间的距离，让“非遗”美食卖得更远</strong></span></p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px; text-align: center;\"> </p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\"><span style=\"color: rgb(255, 102, 0);\"><span style=\"font-size: 18px;\"><strong>济南益康食品项目基本情况</strong></span></span></p>\r\n\r\n<hr>\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\"><strong><span style=\"color: rgb(255, 0, 0);\">上线时间：</span></strong>2014年7月15日<br>\r\n<strong><span style=\"color: rgb(255, 0, 0);\">所属行业：</span></strong>食品行业<br>\r\n<span style=\"color: rgb(255, 0, 0);\"><strong>盈利模式：</strong></span>团购批发、招商加盟<br>\r\n<span style=\"color: rgb(255, 0, 0);\"><strong>营销型网址：</strong></span><a href=\"http://www.yikang1977.com/\" style=\"color: rgb(182, 18, 18); text-decoration: none;\" target=\"_blank\"><span style=\"color: rgb(255, 0, 0);\"><strong>http://www.yikang1977.com</strong></span></a><br>\r\n<span style=\"color: rgb(255, 0, 0);\"><strong>美调服务团队：</strong></span>任溪、倪文瑜、谢恩、李亚娟、裴伟 、刘天易 、周泽航</p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\"><span style=\"color: rgb(255, 102, 0);\"><span style=\"font-size: 18px;\"><strong>合作之前所遇到的问题：</strong></span></span></p>\r\n\r\n<hr>\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\">  1、已有一定知名度的区域品牌，能否成为全国品牌？<br>\r\n  2、食品行业能否做网络营销，如何拓展客户资源？<br>\r\n  3、免费推广、付费推广先做谁？</p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\"><span style=\"color: rgb(255, 102, 0);\"><span style=\"font-size: 18px;\"><strong>为什么选择做网络营销：</strong></span></span></p>\r\n\r\n<hr>\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\">  企业依靠传统的线下模式开展团购批发、招商加盟业务，客户局限在济南，想增加销售业绩，提升品牌知名度，把“非遗”月饼等美食产品卖往全国各地</p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\"><span style=\"color: rgb(255, 102, 0);\"><span style=\"font-size: 18px;\"><strong>网络营销带来的收获：</strong></span></span></p>\r\n\r\n<hr>\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\">  付费推广第2天接到询盘，20天后接到第一个订单，2014年中秋节共接到60个订单，50%的订单来自济南以外地区，接到河北、四川等省的订单，扩大了销售渠道和客户来源，实现了产品走出济南的目标，提升了品牌知名度。也接到1个订购汤圆的大单，因为产能不够被迫放弃。</p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\"><span style=\"color: rgb(255, 102, 0);\"><span style=\"font-size: 18px;\"><strong><strong>济南益康食品</strong>营销型网站解析：</strong></span></span></p>\r\n\r\n<hr>\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\"> </p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px; text-align: center;\"><img alt=\"/templetes/meetiao/img/fir.jpg\" class=\"img-responsive\" src=\"http://img.nsw88.com/FCK/2015-07/20150715HRZ2420RD8.jpg\"><br>\r\n  首屏从文化、生产、销售、合作商家来展示“葡萄软月”月饼的独特性和优势。</p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px; text-align: center;\"><br>\r\n <img alt=\"/templetes/meetiao/img/yue.jpg\" class=\"img-responsive\" src=\"http://img.nsw88.com/FCK/2015-07/20150715TFV082T688.jpg\"><br>\r\n图文并茂介绍葡萄软月月饼的卖点，让客户一下子就记住品牌。</p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px; text-align: center;\"><br>\r\n <img alt=\"/templetes/meetiao/img/yue2.jpg\" class=\"img-responsive\" src=\"http://img.nsw88.com/FCK/2015-07/20150715ZF2620TF60.jpg\"><br>\r\n多种分类方法结合，图文并茂，展示产品下面都设置咨询探头，既方便客户了解查找，又增加询盘。</p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px; text-align: center;\"><br>\r\n <img alt=\"/templetes/meetiao/img/adv.jpg\" class=\"img-responsive\" src=\"http://img.nsw88.com/FCK/2015-07/201507152P62604H2H.jpg\"><br>\r\n5个方面解读加盟的优势，让想加盟的客户打消疑虑，嵌入获取客户手机号码的线索框和QQ，便于抓住有意向的客户，获取更多有效线索。</p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px; text-align: center;\"><br>\r\n <img alt=\"/templetes/meetiao/img/sec.jpg\" class=\"img-responsive\" src=\"http://img.nsw88.com/FCK/2015-07/20150715ZP8D8PXH20.jpg\"><br>\r\n消费者感言和客户案例相结合，更真实，增加说服力，让客户对产品销售和盈利有信心。</p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px; text-align: center;\"><br>\r\n <img alt=\"/templetes/meetiao/img/zheng.jpg\" class=\"img-responsive\" src=\"http://img.nsw88.com/FCK/2015-07/20150715082F626P8P.jpg\"><br>\r\n各种荣誉资质展示，提升公司公信力。</p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\"><span style=\"color: rgb(255, 102, 0);\"><span style=\"font-size: 18px;\"><strong>网络营销成功经验：</strong></span></span></p>\r\n\r\n<hr>\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\">  1、老板重视，全程参与。2014年上完单仁集团策略班，立刻实施网络营销计划，找牛商网建营销型网站，网站一上线，就开始投付费推广。<br>\r\n  2、百度精准投放。月饼团购批发，月饼厂家，月饼团购，月饼批发，月饼代工，月饼代理，月饼品牌精准投放，排名在前三名。3付费推广第2天接到询盘，20天后接到第一个订单，2014年中秋节共接到60个订单。<br>\r\n  3、免费与付费相结合：网站刚上线初期，没有排名没有收录，付费是最快捷最有成效的；免费推广必须坚持不懈，软文数量和质量要并重，网站资讯要天天更新，最好是原创，原创收录效果最好。<br>\r\n  4、多发软文资讯到B2B平台，收录快。<br>\r\n  5、视频营销效果很好，必须重视并坚持做，收录特别快。</p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\"><span style=\"color: rgb(255, 102, 0);\"><span style=\"font-size: 18px;\"><strong>济南益康食品负责人的分享：</strong></span></span></p>\r\n\r\n<hr>\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\">  第一次接触营销型网站，第一次做网络营销，觉得很好，但是心里没底，身边的人也都有疑惑能不能有效果。<img alt=\"/templetes/meetiao/img/ren4.jpg\" class=\"img-responsive\" src=\"http://img.nsw88.com/FCK/2015-07/2015071564V0822288.jpg\"><br>\r\n  开弓没有回头箭，开始了就得坚持下去。网上做推广第二天就接到询盘，20天接到第1个订单，整个公司都沸腾了，亲戚朋友都非常开心，鼓励我继续做下去。不到2个月接了60个订单一半左右的客户来自济南以外的地区，品牌知名度也提升了，出乎意料。<br>\r\n  2015年，计划把网上销售的业绩翻一番。</p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\"><span style=\"color: rgb(255, 102, 0);\"><span style=\"font-size: 18px;\"><strong>案例亮点：</strong></span></span></p>\r\n\r\n<hr>\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\">  1、将区域品牌打造成全国品牌：网站上线时，离中秋节不到2个月，很多公司厂商已经下好订单采购，剩下的时间和销售机会，非常有限，因为推广运营做得高效，还是抢在中秋节前接到60个订单，一半的客户来自济南以外地区。<br>\r\n  2、虽然第一次做网络营销，没有经验，有疑惑，还是坚持做下去。<br>\r\n  3、付费推广先行，迅速带来精准流量和有效询盘。<br>\r\n  4、掌握了免费推广的技巧方法。</p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\"><span style=\"color: rgb(255, 102, 0);\"><span style=\"font-size: 18px;\"><strong>济南市益康食品厂有限公司介绍：</strong></span></span></p>\r\n\r\n<hr>\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\">  济南市益康食品厂有限公司1977年建厂，是以生产月饼、粽子、汤圆、水果蛋糕、桃酥、面包、生日蛋糕等中西糕点、面食及速冻食品为主的大型食品加工企业。是济南市食品行业龙头企业，2004年跻身于山东省食品百强企业，通过了ISO9001质量管理体系认证，商标被评为“山东省著名商标”，是中国著名月饼品牌企业，2012年“葡萄软月”月饼被评为“济南市特产月饼”并于同年被列入济南市非物质文化遗产名录，2013年荣获“最受消费者欢迎食品品牌”称号，2014年被推举为中华糕饼优秀品牌100强，同年被山东省商务厅授予山东省老字号企业。</p>\r\n</div>\r\n', '2016-03-28 04:04:26', '2016-04-13 07:38:10', null, null, '个', null, '');
INSERT INTO `admin_pages` VALUES ('15', '1', '4', '叙总统阿萨德:叙重建将仰仗中国俄罗斯和伊朗', 'xu-zong-ton', '', '', '据俄罗斯卫星网3月30日报道，叙利亚总统阿萨德在接受俄媒采访时表示，该国和伊拉克的恐怖组织由土耳其、沙特及英法等部分欧洲国家直接支持。叙利亚重建将仰仗中国、俄罗斯和伊朗三国。', '/templetes/meetiao/assets/blog/on_the_mountains.jpg', '<p>阿萨德表示，恐怖主义是真正的问题，在叙利亚和伊拉克都有恐怖组织。这些组织直接受到土耳其和沙特阿拉伯王室的支持，也受到一些欧洲国家，尤其是法国和英国的支持。</p>\r\n\r\n<div class=\"otherContent_01\" id=\"ad_44089\" style=\"display: block; margin: 10px 20px 10px 0px; float: left; overflow: hidden; clear: both; padding: 4px; width: 300px; height: 250px;\"> </div>\r\n\r\n<p>　　在谈到叙利亚重建的问题时，阿萨德表示，在该国重建过程中，大马士革将仰仗俄罗斯、中国和伊朗。</p>\r\n\r\n<p>　　他说：“重建进程中我们期望仰仗在此次危机期间支持叙利亚的三个国家：俄罗斯、中国和伊朗。我们首先欢迎这三个国家的公司，尤其是俄罗斯公司。我认为俄罗斯公司将拥有非常广阔的空间来为叙利亚的重建做贡献。</p>\r\n', '2016-03-31 04:35:25', '2016-03-31 04:35:25', null, null, null, null, null);
INSERT INTO `admin_pages` VALUES ('16', '1', '4', '叙总统阿萨德:叙重建将仰仗中国俄罗斯和伊朗', '22', '', '', '据俄罗斯卫星网3月30日报道，叙利亚总统阿萨德在接受俄媒采访时表示，该国和伊拉克的恐怖组织由土耳其、沙特及英法等部分欧洲国家直接支持。叙利亚重建将仰仗中国、俄罗斯和伊朗三国。', '/templetes/meetiao/assets/blog/on_the_mountains.jpg', '<p>阿萨德表示，恐怖主义是真正的问题，在叙利亚和伊拉克都有恐怖组织。这些组织直接受到土耳其和沙特阿拉伯王室的支持，也受到一些欧洲国家，尤其是法国和英国的支持。</p>\r\n', '2016-03-31 05:29:25', '2016-03-31 05:56:53', null, null, null, null, null);
INSERT INTO `admin_pages` VALUES ('17', '1', '2', 'APP开发', 'APP-kai-fa', '', 'app', 'App开发，是指专注于手机应用软件开发与服务', '', '<p>App开发，是指专注于手机应用软件开发与服务。App是application的缩写，通常专指手机上的应用软件，或称手机客户端。苹果公司的App store开创了手机软件业发展的新篇章，使得第三方软件的提供者参与其中的积极性空前高涨。随着智能手机越发普及、用户越发依赖手机软件商店，App开发的市场需求与发展前景也逐渐蓬勃，截止到2012年12月，App开发已变为红海市场。<br>\r\n当然移动互联网时代是全民的移动互联网时代，是每个人的时代，也是每个企业的时代。APP便捷了每个人的生活，APP开发让每个企业都开始了移动信息化进程</p>\r\n', '2016-03-31 07:35:08', '2016-03-31 07:35:08', null, null, null, null, null);
INSERT INTO `admin_pages` VALUES ('18', '1', '2', '应用软件开发', 'ying-yong-r', '', '软件，应用', '应用软件开发', '', '<p>应用软件开发是指使用程序语言C#、 java 、 c++、vb等高级语言编写，主要是用于商业、生活应用的软件的开发。<br>\r\n应用软件它可以拓宽计算机系统的应用领域，放大硬件的功能。其开发应用软件是为满足用户不同领域、不同问题的应用需求而提供的那部分软件。</p>\r\n', '2016-03-31 07:35:51', '2016-03-31 07:35:51', null, null, null, null, null);
INSERT INTO `admin_pages` VALUES ('20', '1', '9', '美调科技', 'zhuan-ye-wa', '', '首页banner页面1', '专注于向餐饮及餐饮供应链企业提供最有深度的市场营销解决方案，是专业的互联网服务解决方案提供商和网络营销服务专家，为客户提供一体化的互联网全网营销解决方案。', '', '<p>...</p>\r\n', '2016-04-07 08:29:05', '2016-04-12 08:03:21', null, null, '个', null, '');
INSERT INTO `admin_pages` VALUES ('21', '1', '9', 'APP开发及应用软件开发', 'APP-kai-fab', '', '首页banner页面3', 'App开发，是指专注于手机应用软件开发与服务。 App是application的缩写，通常专指手机上的应用软件，或称手机客户端。另外目前有很多在线app开发平台。', '', '<p>...</p>\r\n', '2016-04-07 08:32:22', '2016-04-12 08:04:13', null, null, '个', null, '');
INSERT INTO `admin_pages` VALUES ('22', '1', '9', '美调科技', 'SEO-you-hua', '', '首页banner页面2', '专注于向餐饮及餐饮供应链企业提供最有深度的市场营销解决方案，是专业的互联网服务解决方案提供商和网络营销服务专家', '', '<p>...</p>\r\n', '2016-04-07 08:32:47', '2016-04-12 08:03:53', null, null, '个', null, '');
INSERT INTO `admin_pages` VALUES ('23', '1', '10', '蒋欣', 'wen-lin-gan', '', '职业称谓是什么', '蒋欣，1983年5月8日出生于新疆维吾尔自治区乌鲁木齐市，中国内地女演员，毕业于河南艺术职业学院。从1991年开始参加影视剧拍摄，1991年其主演的电视剧《坠子皇后》播出；2001年出演电视剧《大脚马皇后》，2003年蒋欣在张纪中版《天龙八部》饰演木婉清而走红。2004年出演偶像古装剧《仙剑奇侠传》和《欢天喜地七仙女》，2011年在古装清宫斗争剧《甄嬛传》中饰演华妃而受到广泛关注并获得安徽卫视国剧盛典最佳女配角奖等多个奖项。2013年参演电视剧《华胥引之绝爱之城》', '/templetes/meetiao/assets/tou/tou1.jpg', '<p>。。。</p>\r\n', '2016-04-08 03:16:23', '2016-04-12 02:50:31', null, null, '个', null, '');
INSERT INTO `admin_pages` VALUES ('24', '1', '10', '路人甲', 'guan-yu', '', '职业称谓是什么', '关于路人甲的个人简介', '/templetes/meetiao/assets/tou/tou5.jpg', '<p>#.</p>\r\n', '2016-04-08 03:23:37', '2016-04-12 03:00:16', null, null, '个', null, '');
INSERT INTO `admin_pages` VALUES ('25', '1', '10', '韩庚', 'zhao-yun', '', '职业称谓是什么', '韩庚是第一位韩国出道的中国艺人，2005年于组合Super Junior唯一中国籍成员出道。2008年担任其子组合SJ-M的队长，2009年12月21日与韩国SM娱乐有限公司解约并回国发展。2010年7月发行首张个人专辑《庚心》，重新出发。9月9日首部主演电影《大武生》上映，表现获肯定。2012年7月发行第二张专辑《寒更》，并于11月12日在MTV欧洲音乐奖上获得全球最佳艺人奖。2013年3月24日获KCA美国儿童选择奖最受欢迎艺人。', '/templetes/meetiao/assets/tou/tou2.jpg', '<p>。。.</p>\r\n', '2016-04-08 03:26:24', '2016-04-12 02:54:01', null, null, '个', null, '');
INSERT INTO `admin_pages` VALUES ('26', '1', '10', '罗志祥', 'zhu-ge-lian', '', '职业称谓是什么', '于1994年出道，2003年罗志祥推出首张个人专辑《Show Time》，2005年举行首场个人演唱会，成为第一位踏上台北小巨蛋的流行歌手。2008年发行第六张专辑《潮男正传》，获MTV亚洲音乐大奖。2010年专辑《罗生门》获“五白金”。2012年前往日本乐坛发展，推出首张日文单曲《Dante》，《Dante》推出首周即登上日本公信榜单曲周榜第十名，使罗志祥成为首位于日本公信榜单曲获得前十名的台湾男歌手', '/templetes/meetiao/assets/tou/tou4.jpg', '<p>。..</p>\r\n', '2016-04-08 03:26:46', '2016-04-12 02:57:27', null, null, '个', null, '');
INSERT INTO `admin_pages` VALUES ('27', '1', '10', '王心凌', 'zhang-fei', '', '职业称谓是什么', '2000年主演客家语电视短剧《车正在追》。2003年发行首张个人专辑《Cyndi Begin》。2011年凭借《爱上查美乐》获得华鼎奖偶像励志类题材最佳女主角。2012年11月30日加盟环球唱片并发行首张专辑《爱不爱》。2013年7月相隔两年再拍大型偶像剧《幸福选择题》。2014年7月25日发行个人第十张专辑《第10个王心凌》；同年12月在微电影《爱的圆舞曲》中饰演一名芭蕾舞者。', '/templetes/meetiao/assets/tou/tou6.jpg', '<p>。。。</p>\r\n', '2016-04-08 03:27:09', '2016-04-12 02:59:06', null, null, '个', null, '');
INSERT INTO `admin_pages` VALUES ('28', '1', '3', '深深山香菇', 'shen-shen-s', '', '香菇', '', '/templetes/meetiao/img/xiang.png', '<h1 style=\"margin: 0px; padding: 0px; font-size: 20px; text-align: center; line-height: 35px; color: rgb(102, 102, 102); font-family: \'Microsoft YaHei\';\">深深山香菇种植专业合作社营销型网站案例展示</h1>\r\n\r\n<div id=\"cntrBody\" style=\"margin: 0px; padding: 0px; font-family: \'Microsoft YaHei\'; font-size: 12px; line-height: 14.772px;\">\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\"> </p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\"><span style=\"color: rgb(255, 102, 0);\"><span style=\"font-size: 18px;\"><strong>深深山香菇种植专业合作社项目情况</strong></span></span></p>\r\n\r\n<hr>\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\"><strong>  上线时间</strong>：2014年8月</p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\"><strong>  所属行业</strong>：食用菌</p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\"><strong>  盈利模式</strong>：展示销售</p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\">  <strong>企业网址</strong>：www.xxhlg.com</p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\"><br>\r\n   </p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\"><span style=\"color: rgb(255, 102, 0);\"><span style=\"font-size: 18px;\"><strong>深深山香菇种植专业合作社<b>项目特点介绍</b></strong></span></span></p>\r\n\r\n<hr>\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\"><b>　　1、2013年3月1日开始做百度竞价，但是不够专业，网络转化量较低。同时因为原网站制作技术不够专业，网站关键词设置不够好，网站自然排名不好。<br>\r\n<br>\r\n  2、在听了单仁资讯的课程以后，发现原有网站不能够系统的解决客户关心的“卖什么？为什么选择我？为什么信任我？”三大问题。所以就坚定不移的选择了牛商网建设营销型网站。<br>\r\n<br>\r\n  3、新网站2014年7月25日试运行，8月10日正式上线。在试运行期间就接到了沃尔玛中国300万元香菇的意向订单，和三全食品30万元香菇腿的订单。<br>\r\n<br>\r\n  4、网站上线后来自全国的订单更是如雪片般飞来,如上海的早阳包子年度合同30吨240万元；上海的吉祥馄饨30吨240万元等等。</b></p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\"><b> </b></p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\"><b><strong>  ——</strong><b>　营销型网站助您在网络营销的道路上腾飞！</b><br>\r\n </b></p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\"><b> </b></p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\"><b><font face=\"Arial, Verdana, sans-serif\"><b>  <strong>深深山香菇种植专业合作社</strong></b></font><font face=\"Arial, Verdana, sans-serif\"><b>营销型网站策划</b></font></b></p>\r\n\r\n<hr>\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\"><b> <span style=\"font-family: Arial, Verdana, sans-serif;\"> <img alt=\"/templetes/meetiao/img/xianggu.png\" class=\"img-responsive\" src=\"http://img.nsw88.com/FCK/2014-09/20140926RZF6TBT62F.png\"></span></b></p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\"><b> </b></p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\"><b> 　<strong>主广告位区域体现公司核心产品和核心服务，让客户第一眼知道我们是做什么的。</strong></b></p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\"><b>　　<img alt=\"/templetes/meetiao/img/chanpin.png\" class=\"img-responsive\" src=\"http://img.nsw88.com/FCK/2014-09/201409262VP6X4D8L8.png\"></b></p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\"><b> </b></p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\"><b><strong>  </strong>　<strong>产品分类一是按照客户需求分类，方便客户对号入座；二是有利于关键词优化。</strong><br>\r\n </b></p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\"><b>  <img alt=\"/templetes/meetiao/img/advatage.png\" class=\"img-responsive\" src=\"http://img.nsw88.com/FCK/2014-09/20140926LNVH42B40P.png\"><br>\r\n<br>\r\n  <strong>欢乐菇优势对比主要是核心产品优势PK以及客户服务PK。</strong><br>\r\n<br>\r\n<img alt=\"/templetes/meetiao/img/reason.png\" class=\"img-responsive\" src=\"http://img.nsw88.com/FCK/2014-09/2014092686HD0XH64J.png\">　<img alt=\"/templetes/meetiao/img/reason1.png\" class=\"img-responsive\" src=\"http://img.nsw88.com/FCK/2014-09/20140926PD2T6D8Z8V.png\"></b></p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\"><b>　　<strong>俗话说“王婆卖瓜，自卖自夸”，此处公信力的展示，必定让客户眼前一亮！</strong><br>\r\n<br>\r\n<img alt=\"/templetes/meetiao/img/jianzheng.png\" class=\"img-responsive\" src=\"http://img.nsw88.com/FCK/2014-09/20140926NH0464B8ZP.png\"></b></p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\"><b> </b></p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\"><b>　　<strong>客户见证结合公司场景图片，更有说服力！进一步打消客户疑虑。</strong></b></p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\"><b> </b></p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\"><b><strong style=\"font-family: Arial, Verdana, sans-serif;\">  </strong><strong style=\"font-family: Arial, Verdana, sans-serif;\"><b><strong>深深山香菇种植专业合作社</strong></b>营销型网站运营经验</strong></b></p>\r\n\r\n<hr>\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\"><b> </b></p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\"><b>　<strong>　1、优化推广：</strong><br>\r\n<br>\r\n  结合公司的营销型网站，在网络平台上对公司品牌以及产品做全方位、各种渠道的宣传，引入有效流量，获取有效询盘并最终达到成交。<br>\r\n<br>\r\n<strong>　 2、团队建设：</strong><br>\r\n<br>\r\n  及时完善网站上线以后的运营培训和实践，快速让部门的每一个人明白自己该干什么，怎么干。让网站发挥它最大的作用。</b></p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\"><b> </b></p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\"><b> </b></p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\"><span style=\"color: rgb(255, 102, 0);\"><span style=\"font-size: 18px;\"><strong>深深山香菇种植专业合作社总经理杨总分享</strong></span></span></p>\r\n \r\n\r\n<p> </p>\r\n\r\n<hr style=\"font-family: Arial, Verdana, sans-serif;\">\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px; text-align: center;\"><b> </b></p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px; text-align: center;\"><b><img alt=\"/templetes/meetiao/img/ren.jpg\" class=\"img-responsive\" src=\"http://img.nsw88.com/FCK/2014-09/20140926BVF2JX6X2N.jpg\"> </b></p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\"><b>　　要立即下定决心做营销型网站！不管你的其他股东是否认同，等你做了有了好效果其他股东都会说“现在看来当时还是您的决定是正确的呀！”迈出这一步十分重要！<br>\r\n<br>\r\n  让网络营销人员和您公司的全体营销人员参与到网站建设过程中来，经过反复的讨论修改，等到网站建设好以后，他们一定会像对待自己的孩子一样对待网站了，不但有深度的认知，而且有了很深的感情。<br>\r\n<br>\r\n </b></p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\"><b> </b></p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\"><span style=\"color: rgb(255, 102, 0);\"><span style=\"font-size: 18px;\"><strong>深深山香菇种植专业合作社公司介绍</strong></span></span></p>\r\n\r\n<hr style=\"font-family: Arial, Verdana, sans-serif;\">\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\"><b>　　西峡县惠多利商贸有限公司公司成立三年。是西峡香菇行业第一家专业运用互联网营销的公司。获得了中国食用菌商务网04年度优秀品牌殊荣。<br>\r\n<br>\r\n  深深山香菇种植专业合作社香菇腿是西峡境内，第一家以仲景、康师傅、统一等企业的高质量指标，在流水线上，经过严格企业化管理生产出来的产品。西峡没有第二家如此的规模、如此的管理、如此的质量意识与服务意识的企业。<br>\r\n<br>\r\n  深深山香菇种植专业合作社的香菇是根据全国速冻、餐饮、深加工、外贸等企业的不同等级、规格需要，进行针对性的定制。确保满足不同客户的不同需求。两种产品，在同品质、同综合条件下我们的产品价格是最低的。</b></p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\"><b> </b></p>\r\n\r\n<p style=\"margin: 18px 0px; padding: 0px 10px; word-spacing: 10px; font-size: 14px; line-height: 26px;\"><b> </b></p>\r\n</div>\r\n', '2016-04-13 03:59:07', '2016-04-13 05:21:43', null, null, '个', null, '');
INSERT INTO `admin_pages` VALUES ('29', '1', '3', '果C益佰', 'guo-C-yi-ba', '', '果C益佰', '果C益佰', '/templetes/meetiao/img/LOGO.png', '<h1 style=\"margin: 0px 0px 6px; padding: 0px; font-size: 26px; text-align: center; color: rgb(0, 0, 0);\">广州果C益佰营销型网站建设案例</h1>\r\n\r\n<p> </p>\r\n\r\n<div id=\"cntrBody\" style=\"margin: 0px; padding: 0px 20px; font-size: 14px;\">\r\n<p style=\"margin: 0px; padding: 0px; line-height: 25px; font-family: arial, helvetica, clean, sans-serif;\">　　<span style=\"font-size: 18px; color: rgb(112, 48, 160);\"><strong>广州果C益佰营销型网站建设项目介绍</strong></span></p>\r\n\r\n<hr style=\"font-family: arial, helvetica, clean, sans-serif; line-height: 17.234px;\">\r\n<p style=\"margin: 0px; padding: 0px; line-height: 25px; font-family: arial, helvetica, clean, sans-serif;\">　　【所属行业】：食品</p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; line-height: 25px; font-family: arial, helvetica, clean, sans-serif;\">　　【网站盈利模式】：招商加盟</p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; line-height: 25px; font-family: arial, helvetica, clean, sans-serif;\">　　【主营产品/推广关键词介绍】：香港水果捞招商、甜品代理、甜品店加盟、甜品加盟、港式甜品店、港式甜品、港式甜品加盟、港式甜品店加盟</p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; line-height: 25px; font-family: arial, helvetica, clean, sans-serif;\">　　【参考工时】：50个工作日</p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; line-height: 25px; font-family: arial, helvetica, clean, sans-serif;\"> </p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; line-height: 25px; font-family: arial, helvetica, clean, sans-serif;\">　　<span style=\"color: rgb(112, 48, 160);\"><strong><span style=\"font-size: 18px;\">广州果C益佰简介</span></strong></span></p>\r\n\r\n<hr style=\"font-family: arial, helvetica, clean, sans-serif; line-height: 17.234px;\">\r\n<p style=\"margin: 0px; padding: 0px; line-height: 25px; font-family: arial, helvetica, clean, sans-serif;\">　　果C益佰源自香港，始创于1987年，2007年引入内地，凭借着和顺公司完善的产品研发能力和成熟完整的连锁管理体系，以及十八年连锁管理中积累的丰富经营经验，果C益佰所到之处广受追捧，经营十分火爆，成为当地一种美食现象和文化。在激烈的市场竞争中，果C益佰以品质胜出，成为港式甜品三大品牌之一，是港式甜品的创导者之一，品牌美誉度有口皆碑。</p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; line-height: 25px; font-family: arial, helvetica, clean, sans-serif;\"> </p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; line-height: 25px; font-family: arial, helvetica, clean, sans-serif;\">　　果C益佰门店遍布北京、香港、广东、海南、福建、浙江、江苏、山东、辽宁、云南、江西、湖南、湖北、四川、重庆、宁夏、内蒙等地区，现有分店400多家，全国有50多家代理商，2014年北京公司和北京直营店同时开业，形成了北以北京为中心，南以广州为中心的全国格局，更好的服务全国加盟商。</p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; line-height: 25px; font-family: arial, helvetica, clean, sans-serif;\"> </p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; line-height: 25px; font-family: arial, helvetica, clean, sans-serif;\">　　果C益佰致力于时尚港式甜品文化的传播，打造中国甜品领军品牌。多年来，果C益佰以一流的品牌形象、深厚的港式甜品文化为底蕴，特别注重科学的饮食搭配，口感滑爽、色彩缤纷、造型独特、水果新鲜、营养丰富，以现做现卖的方式，把健康的营养餐品和时尚的港式甜品文化带给了每位消费者。</p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; line-height: 25px; font-family: arial, helvetica, clean, sans-serif;\"> </p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; line-height: 25px; font-family: arial, helvetica, clean, sans-serif;\"> </p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; line-height: 25px; font-family: arial, helvetica, clean, sans-serif;\">　　<span style=\"color: rgb(112, 48, 160);\"><strong><span style=\"font-size: 18px;\">广州果C益佰营销型网站建设案例欣赏</span></strong></span></p>\r\n\r\n<hr style=\"font-family: arial, helvetica, clean, sans-serif; line-height: 17.234px;\">\r\n<p style=\"margin: 0px; padding: 0px; line-height: 25px; font-family: arial, helvetica, clean, sans-serif; text-align: center;\"><img alt=\"/templetes/meetiao/img/guoc.jpg\" class=\"img-responsive\" src=\"http://www.shenduwang.com/UploadFiles/Product/2015-01-22/20150122161037127.jpg\" title=\"广州果C益佰营销型网站建设案例\"></p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; line-height: 25px; font-family: arial, helvetica, clean, sans-serif;\"> </p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; line-height: 25px; font-family: arial, helvetica, clean, sans-serif;\"><span style=\"background-color: rgb(255, 255, 0);\">果C益佰营销型网站网址：http://www.hkgc100.com/</span></p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; line-height: 25px; font-family: arial, helvetica, clean, sans-serif;\"> </p>\r\n</div>\r\n', '2016-04-13 06:09:32', '2016-04-13 07:41:18', null, null, '个', null, '');
INSERT INTO `admin_pages` VALUES ('30', '1', '3', '金山鲜果行', 'jin-shan-xi', '', '鲜果', '', '/templetes/meetiao/img/mang.jpg', '<h1 style=\"margin: 0px 0px 6px; padding: 0px; font-size: 26px; text-align: center; color: rgb(0, 0, 0); font-family: arial, helvetica, clean, sans-serif;\">广州市金山鲜果行营销型网站案例</h1>\r\n\r\n<div class=\"info_con_tit\" style=\"margin: 0px 0px 15px; padding: 0px 0px 20px; font-family: 宋体; line-height: 24px; border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(204, 204, 204); color: rgb(65, 65, 65); font-size: 12px;\">\r\n<div class=\"n_tit\" style=\"margin: 0px; padding: 0px; text-align: center; color: rgb(126, 126, 126);\"> </div>\r\n</div>\r\n\r\n<div id=\"cntrBody\" style=\"margin: 0px; padding: 0px 20px; font-size: 14px; font-family: arial, helvetica, clean, sans-serif;\">\r\n<p style=\"margin: 0px; padding: 0px; line-height: 25px;\">       广州金山鲜果行成立于1989年，成立初期是以专营国产水果为主的小型贸易公司。公司初期的八年时间,与全国各地客商建立起良好的合作关系，也建成了全面完善的销售网络.1997年后，公司进行了业务调整，把重点转移到进口水果，以及温室瓜果，及观赏工艺瓜果（包括方西瓜，巨型南瓜，观赏南瓜等）等业务上。到目前，本果行已经成为一间主要经营温室瓜果，观赏工艺瓜果，兼營世界各地水果，并提供现代农业技术咨询及瓜果销售服务的综合性农业开发销售公司。</p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; line-height: 25px; text-align: center;\"><img alt=\"/templetes/meetiao/img/shui.jpg\" class=\"img-responsive\" src=\"http://www.shenduwang.com/UploadFiles/FCK/600%E9%87%91%E5%B1%B1%E9%B2%9C%E6%9E%9C%E8%A1%8C%E9%A6%96%E9%A1%B5%E8%AE%BE%E8%AE%A1%E7%A8%BF.jpg\"></p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; line-height: 25px;\">经过21年的发展，公司现有一个销售公司，位于广州江南果菜批发市场；两个下属高科技农业生产基地和一个果蔬销售连锁机构，分别是：江西安远金宝瓜果生产基地——位于江西省安远县车头镇；江西赣州市欢乐农家现代农业有限公司——位于江西赣州会昌县小密乡；世果汇果蔬休闲连锁店——位于广东佛山金沙洲。共有员工近150人。除了本公司的生产基地外，同时本公司和广东，海南，福建，广西等省份近30个高科技农业生产基地建立了良好的合作关系，可常年大量提供近20多个品种的温室瓜果，如西瓜类麒麟西瓜，特小凤，墨童小型无籽西瓜等，洋香瓜类的日本翠蜜，巴黎四号等。除以上产品，本公司还斥巨资开发引进了国外流行的迷你观赏瓜果类和迷你食用南瓜类等品种，有样式各异的观赏玩具南瓜，还有重达100公斤以上的巨型南瓜，都受到市场的欢迎和追捧。</p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; line-height: 25px;\"><span style=\"background-color: rgb(255, 255, 0);\">金山鲜果行网址：www.janbaofruit.com</span></p>\r\n</div>\r\n', '2016-04-13 06:18:15', '2016-04-13 07:40:30', null, null, '个', null, '');
INSERT INTO `admin_pages` VALUES ('31', '1', '3', '特产优联', 'te-chan-you', '', '', '', '/templetes/meetiao/img/te.jpg', '<div class=\"word_title\" style=\"margin: 0px auto; padding: 0px; vertical-align: baseline; font-family: 微软雅黑, 宋体, sans-serif; font-size: 12px; color: rgb(102, 102, 102); outline: 0px; width: 705px; line-height: 21.6px; background-image: initial; background-attachment: initial; background-size: initial; background-origin: initial; background-clip: initial; background-position: initial; background-repeat: initial;\">\r\n<h1 style=\"margin: 0px; padding: 10px; vertical-align: baseline; font-family: inherit; font-style: inherit; font-size: 24px; color: inherit; outline: 0px; text-align: center; background: transparent;\">隐藏在浮躁都市中的静谧之地——特产优联</h1>\r\n\r\n<div class=\"word_tag\" style=\"margin: 10px 0px 0px; padding: 0px; vertical-align: baseline; font-family: inherit; font-weight: inherit; font-style: inherit; font-size: 12px; color: inherit; outline: 0px; text-align: center; background: transparent;\"> </div>\r\n</div>\r\n\r\n<div class=\"word_content\" style=\"margin: 0px; padding: 30px; vertical-align: baseline; font-family: 微软雅黑, 宋体, sans-serif; font-size: 12px; color: rgb(102, 102, 102); outline: 0px; line-height: 21.6px; background-image: initial; background-attachment: initial; background-size: initial; background-origin: initial; background-clip: initial; background-position: initial; background-repeat: initial;\">\r\n<p style=\"margin: 0px; padding: 0px; vertical-align: baseline; font-family: inherit; font-weight: inherit; font-style: inherit; font-size: 12px; color: inherit; outline: 0px; background: transparent;\"><strong><span style=\"font-family: 微软雅黑; font-size: 14px;\">      前言</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">：</span></strong></p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; vertical-align: baseline; font-family: inherit; font-weight: inherit; font-style: inherit; font-size: 12px; color: inherit; outline: 0px; background: transparent;\"><span style=\"font-family: 微软雅黑; font-size: 14px;\">      在美调</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">看来，<a href=\"http://www.mymkj.com/wzjs/scwz.html\" style=\"margin: 0px; padding: 0px; vertical-align: baseline; font-family: inherit; font-weight: inherit; font-style: inherit; color: rgb(0, 176, 240); outline: 0px; border: none; cursor: pointer; background: transparent;\" target=\"_blank\" title=\"商城网站建设\">电商网站建设</a></span><span style=\"font-family: 微软雅黑; font-size: 14px;\">的</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">火热</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">，</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">使得众多独立电商平台</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">如雨后春笋</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">般</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">遍地开花</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">，</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">但</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">在对众多</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">电商</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">网站分析后，我们发现，多数电商网站</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">的</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">设计和建设思路</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">是</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">模仿其他平台而建的</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">。</span></p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; vertical-align: baseline; font-family: inherit; font-weight: inherit; font-style: inherit; font-size: 12px; color: inherit; outline: 0px; background: transparent;\"> </p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; vertical-align: baseline; font-family: inherit; font-weight: inherit; font-style: inherit; font-size: 12px; color: inherit; outline: 0px; background: transparent;\"><span style=\"font-family: 微软雅黑; font-size: 14px;\">     在美调</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">看来，</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">这也是</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">我国</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">众多</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">电商</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">平台</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">做不好的原因之一，</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">也是</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">众多电商平台不盈利的</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">根源</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">之一，</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">但</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">在</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">这种背景</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">下，我们</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">发现</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">了</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">隐藏在浮躁都市</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">中的静谧之地——</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">特产优联。</span></p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; vertical-align: baseline; font-family: inherit; font-weight: inherit; font-style: inherit; font-size: 12px; color: inherit; outline: 0px; background: transparent;\"> </p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; vertical-align: baseline; font-family: inherit; font-weight: inherit; font-style: inherit; font-size: 12px; color: inherit; outline: 0px; background: transparent;\"><span style=\"font-family: 微软雅黑; font-size: 14px;\">     今天，我们</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">将</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">带您一道赏析</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">一个特产领域</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">的商城网站</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">，</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">带您领略专注的力量</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">。</span></p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; vertical-align: baseline; font-family: inherit; font-weight: inherit; font-style: inherit; font-size: 12px; color: inherit; outline: 0px; background: transparent;\"> </p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; vertical-align: baseline; font-family: inherit; font-weight: inherit; font-style: inherit; font-size: 12px; color: inherit; outline: 0px; background: transparent;\"><span style=\"font-family: 微软雅黑; font-size: 14px;\">     案例品牌</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">：特产优联</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">；</span></p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; vertical-align: baseline; font-family: inherit; font-weight: inherit; font-style: inherit; font-size: 12px; color: inherit; outline: 0px; background: transparent;\"><span style=\"font-family: 微软雅黑; font-size: 14px;\">     所属</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">类型：电子商务</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">-特产类；</span></p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; vertical-align: baseline; font-family: inherit; font-weight: inherit; font-style: inherit; font-size: 12px; color: inherit; outline: 0px; background: transparent;\"><span style=\"font-family: 微软雅黑; font-size: 14px;\">     友情链接网站域名</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">：</span><a href=\"http://www.techan.com/\" rel=\"nofollow\" style=\"margin: 0px; padding: 0px; vertical-align: baseline; font-family: inherit; font-weight: inherit; font-style: inherit; font-size: 12px; color: rgb(0, 176, 240); outline: 0px; border: none; cursor: pointer; background: transparent;\" target=\"_blank\"><span style=\"font-size: 14px;\">http://www.techan.com/</span></a></p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; vertical-align: baseline; font-family: inherit; font-weight: inherit; font-style: inherit; font-size: 12px; color: inherit; outline: 0px; background: transparent;\"><span style=\"font-family: 微软雅黑; font-size: 14px;\">     域名</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">年龄：</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">13年；</span></p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; vertical-align: baseline; font-family: inherit; font-weight: inherit; font-style: inherit; font-size: 12px; color: inherit; outline: 0px; background: transparent;\"><span style=\"font-family: 微软雅黑; font-size: 14px;\">     设计风格</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">：扁平风</span></p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; vertical-align: baseline; font-family: inherit; font-weight: inherit; font-style: inherit; font-size: 12px; color: inherit; outline: 0px; background: transparent;\"><span style=\"font-family: 微软雅黑; font-size: 14px;\">     特点</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">：简洁，清新，便于操作，用户体验好；</span></p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; vertical-align: baseline; font-family: inherit; font-weight: inherit; font-style: inherit; font-size: 12px; color: inherit; outline: 0px; background: transparent;\"><span style=\"font-family: 微软雅黑; font-size: 14px;\">     弱点</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">：</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">页面布局有所欠缺</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">，</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">产品较少</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">；</span></p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; vertical-align: baseline; font-family: inherit; font-weight: inherit; font-style: inherit; font-size: 12px; color: inherit; outline: 0px; background: transparent;\"> </p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; vertical-align: baseline; font-family: inherit; font-weight: inherit; font-style: inherit; font-size: 12px; color: inherit; outline: 0px; background: transparent;\"><span style=\"font-family: 微软雅黑; font-weight: bold; font-size: 14px;\">     首页见解</span><span style=\"font-family: 微软雅黑; font-weight: bold; font-size: 14px;\">：</span></p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; vertical-align: baseline; font-family: inherit; font-weight: inherit; font-style: inherit; font-size: 12px; color: inherit; outline: 0px; text-align: center; background: transparent;\"><img alt=\"/templetes/meetiao/img/te1.jpg\" class=\"img-responsive\" src=\"http://www.mymkj.com/uploads/pro/20150514/14315834998448.jpg\" title=\"特产优联\"></p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; vertical-align: baseline; font-family: inherit; font-weight: inherit; font-style: inherit; font-size: 12px; color: inherit; outline: 0px; text-align: center; background: transparent;\"> </p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; vertical-align: baseline; font-family: inherit; font-weight: inherit; font-style: inherit; font-size: 12px; color: inherit; outline: 0px; text-align: center; background: transparent;\"> </p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; vertical-align: baseline; font-family: inherit; font-weight: inherit; font-style: inherit; font-size: 12px; color: inherit; outline: 0px; background: transparent;\"><span style=\"font-family: 微软雅黑; font-size: 14px;\">     特产优联目前</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">产品并不是很多，但</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">几乎</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">个个都是精品，正所谓，好的产品会说话，好的产品自己会传播，正是这个道理了。</span></p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; vertical-align: baseline; font-family: inherit; font-weight: inherit; font-style: inherit; font-size: 12px; color: inherit; outline: 0px; background: transparent;\"> </p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; vertical-align: baseline; font-family: inherit; font-weight: inherit; font-style: inherit; font-size: 12px; color: inherit; outline: 0px; background: transparent;\"><span style=\"font-family: 微软雅黑; font-size: 14px;\">     上图</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">是用户进入后，首先看到的内容，</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">是</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">特产优联</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">最先</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">推荐的“</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">台湾</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">美食”</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">和另一幅</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">海报</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">的云南特产</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">，</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">在</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">首屏上，首先给用户</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">一个</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">非常清晰的定位</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">：</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">我们就是做特产的</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">网站</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">，</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">您</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">平时买不到的特产都可以在优联购买；</span></p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; vertical-align: baseline; font-family: inherit; font-weight: inherit; font-style: inherit; font-size: 12px; color: inherit; outline: 0px; background: transparent;\"> </p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; vertical-align: baseline; font-family: inherit; font-weight: inherit; font-style: inherit; font-size: 12px; color: inherit; outline: 0px; background: transparent;\"><span style=\"font-family: 微软雅黑; font-size: 14px;\">     目前</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">，特产优联</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">采用</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">的布局方式是</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">DIV+CSS，</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">这是非常利于互联网营销的</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">，</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">但遗憾</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">的是，部分页面</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">使用</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">了Table</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">。</span></p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; vertical-align: baseline; font-family: inherit; font-weight: inherit; font-style: inherit; font-size: 12px; color: inherit; outline: 0px; background: transparent;\"> </p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; vertical-align: baseline; font-family: inherit; font-weight: inherit; font-style: inherit; font-size: 12px; color: inherit; outline: 0px; background: transparent;\"><span style=\"font-family: 微软雅黑; font-size: 14px;\">     首页上能看到的栏目页有：“</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">宫廷共谱</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">”</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">，</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">“</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">巴马生铁锅</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">”</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">，</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">“</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">新疆馆</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">”</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">，</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">“</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">台湾馆</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">”</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">，</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">在导航</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">列表</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">上，其HTML中采用ul+li</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">方式</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">排列，</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">也</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">非常合理，</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">有利于</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">搜索引擎抓取信息，</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">并</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">利于</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">搜索引擎抓取下级页面</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">，从运营</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">的</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">角度而言，</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">特产优联</span><span style=\"font-family: 微软雅黑; font-size: 14px;\">在这里也是考虑得很完善的。</span></p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; vertical-align: baseline; font-family: inherit; font-weight: inherit; font-style: inherit; font-size: 12px; color: inherit; outline: 0px; background: transparent;\"> </p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; vertical-align: baseline; font-family: inherit; font-weight: inherit; font-style: inherit; font-size: 12px; color: inherit; outline: 0px; text-align: center; background: transparent;\"><img alt=\"/templetes/meetiao/img/te2.jpg\" class=\"img-responsive\" src=\"http://www.mymkj.com/uploads/pro/20150514/1431583652134.jpg\" title=\"绿色食品\"></p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; vertical-align: baseline; font-family: inherit; font-weight: inherit; font-style: inherit; font-size: 12px; color: inherit; outline: 0px; background: transp